import React, {Component} from 'react'

// this high order component will ensure that the Wrapped Component
// will always be unmounted, even if React does not have the time to
// call componentWillUnmount function
export function withGracefulUnmount(WrappedComponent) {

    return class extends Component {

        constructor(props){
            super(props);
            this.state = { mounted: false };
            console.log('gracefullComponent: ', this.props.children);
            this.componentGracefulUnmount = this.componentGracefulUnmount.bind(this)
        }


        componentGracefulUnmount(){
            this.setState({mounted: false});

            window.removeEventListener('beforeunload', this.componentGracefulUnmount);
            window.removeEventListener('beforerender', this.componentGracefulUnmount);
        }

        componentWillMount(){
            this.setState({mounted: true})
        }

        componentDidMount(){
            // make sure the componentWillUnmount of the wrapped instance is executed even if React
            // does not have the time to unmount properly. we achieve that by
            // * hooking on beforeunload for normal page browsing
            window.addEventListener('beforeunload', this.componentGracefulUnmount);
            window.addEventListener('beforerender', this.componentGracefulUnmount);
        }

        componentWillUnmount(){
            this.componentGracefulUnmount()
        }

        render(){

            let { mounted }  = this.state;
            if (mounted) {
                return React.createElement(WrappedComponent, ...this.props);
            }
            return null // force the unmount
        }
    }

}

let generatedId = 1;

function generateId() {
    return generatedId++;
}

/**
 * A simple Store which receive all state changes with an event (string).
 */
export class Store {
    /**
     * The constructor.
     * @param {object} initialeState the initial state.
     */
    constructor(initialeState = {}) {
        /**
         * The state of store.
         * @type {object}
         */
        this.state = initialeState;
        /**
         * Don't touch :)
         * @type {Object}
         */
        this.subscribers = {};

        /**
         * Don't touch :)
         * @type {Array}
         */
        this.componentsSubscribes = [];

        /**
         * Don't touch :)
         * @type {Array}
         */
        this.componentsToUnmount = [];
    }

    /**
     * Method to call to change the state.
     * @param {object} newState the new state (you can put only attributes changed).
     * @param {...string} events events to the origin of the state change.
     */
    updateState(newState, ...events) {
        const oldState = this.state;
        this.state = {...this.state, ...newState};

        const eventsToPropagate = events.concat('*');

        let index = eventsToPropagate.length;
        while (index--) {
            const event = eventsToPropagate[index];
            if (this.subscribers[event]) {
                for (const id in this.subscribers[event]) {
                    if (this.subscribers[event].hasOwnProperty(id)) {
                        this.subscribers[event][id](event, this.state, oldState);
                    }
                }
            }
        }
    }

    /**
     * Method to call to subscribe to an event.
     *
     * There is a special event that catches all events : '*'.
     *
     * @param {string} event event to subscribe.
     * @param {function(event: string, state: object, oldState:object)} callback the callback called when receive the event.
     * @param {Component} component use internally to unsubcribe component when node disappear
     * @return {number} the id to put in param of {@link unsubscribe} to unsubscribe.
     */
    subscribe(event, callback = state => {}, component) {
        if (!this.subscribers[event]) {
            this.subscribers[event] = {};
        }
        const id = generateId();
        this.subscribers[event][id] = callback;

        if (component) {
            let componentWrapper = undefined;
            for (const componentAlreadySubcribes of this.componentsSubscribes) {
                if (componentAlreadySubcribes.component === component) {
                    componentWrapper = componentAlreadySubcribes;
                    break;
                }
            }
            if (!componentWrapper) {
                componentWrapper = {
                    component,
                    subscribes: []
                };
                this.componentsSubscribes.push(componentWrapper);
            }
            componentWrapper.subscribes.push({
                event,
                id
            });
        }

        return id;
    }

    /**
     * Unsubscribe.
     * @param {number} id id received at {@link subscribe}
     */
    unsubsribe(id) {
        for (let event of Object.keys(this.subscribers)) {
            if (this.subscribers[event][id]) {
                delete this.subscribers[event][id];
            }
        }
    }

    /**
     * Unsubcribe all subcribers.
     */
    unsubscribeAll() {
        this.subscribers = {};
        this.componentsSubscribes = [];
        this.componentsToUnmount.forEach(component => component.componentDidUnmount());
        this.componentsToUnmount = [];
    }



    /**
     * Unsubscribe.
     * @param {string} event event sent to {@link subscribe}
     * @param {number} id id received at {@link subscribe}
     */
    unsubscribeByEventAndId(event, id) {
        if (this.subscribers[event][id]) {
            delete this.subscribers[event][id];
        }
    }
}

import { Component } from './Component';
import { Store } from './Store';
import { el, renderToDom, renderTo, renderToString } from './renderer';
import { isFunction } from  './util';

/**
 * Can be used as a simple if/else statement info your jsx :
 * ```
 *  SimpleDom.renderTo('container',
 *      <div>
 *          SimpleDom.predicate(myCondition,
 *              <h1>myCondition is true</h1>,
 *              <h1>myCondition is false</h1>
 *          )
 *      </div>
 *  );
 * ```
 * @param {boolean|function():boolean} cond condition to evaluate
 * @param {*} element element to return if cond is true (if it's a function, the result of the function is returned).
 * @param {*} elseElement element to return if cond is false (if it's a function, the result of the function is returned).
 * @return {*} element or elseElement depending of cond value.
 */
export function predicate(cond, element, elseElement) {
    const isTrue = isFunction(cond) ? cond() : cond;
    if (isTrue) {
        return isFunction(element) ? element() : element;
    }
    return isFunction(elseElement) ? elseElement() : elseElement;
}

exports.Component = Component;
exports.Store = Store;
exports.el = el;
exports.renderToDom = renderToDom;
exports.renderTo = renderTo;
exports.renderToString = renderToString;

import { convertToNode } from './converter';
import { flatten, dasherize, isFunction } from './util';
import { Store } from './Store';
import * as ReactDOM from "react-dom";
import React from "react";


/**
 * JSX factory function to create an object representing a dom node. Designed to be used with a JSX transpiler.
 * @param {Object|Component|string|function} element the name of the tag, or a {@link Component}.
 * @param {Object} attrs properties of the node, a plain old JS object. Not optional, if no value, put empty object.
 * @param {Array} children the children of the node, a vararg
 * @return {Object} an object representing a dom node.
 */
export function el(element, attrs, ...children) {
    const isReactComp = elem => elem && elem.prototype && elem.prototype.__proto__.isReactComponent;
    if (element && element.isComponent) {
        const props = {
            ...attrs,
            children: (flatten(children) || []).filter(child => child !== null && child !== undefined)
        };
        return {
            isComponent: true,
            componentClass: element,
            props
        };
    } else if (isReactComp(element)) {
        console.log('children of ' + element.name +': ', children)
        //const simpleDomChildren = children.filter(child => child.isComponent);
        const allChildren = flatten(children).map(child => child.simpleDomChildren || []);
        const simpleDomChildren = flatten(allChildren).concat(children).filter(comp => comp.isComponent);
        let i = 0;
        return {
            isReactComponent: true,
            componentClass: element,
            attrs,
            children: (flatten(children) || [])
                .filter(child => child !== null && child !== undefined)
                .map(child => {
                    if (child.isReactComponent) {
                        return React.createElement(child.componentClass, child.attrs, child.children);
                    } else if (child.isComponent) {
                        const newElem =  React.createElement('div', {'data-attribute': i}, []);
                        ++i;
                        return newElem;
                    }
                    return React.createElement(child.name, child.attrs, child.children);
                })
                .filter(child => child !== null && child !== undefined),
            simpleDomChildren
        };
    } else {
        if (isFunction(element)) {
            return element(attrs, ...children);
        }
        return {
            name: element,
            attrs: attrs || {},
            children: (flatten(children) || []).filter(child => child !== null && child !== undefined),
            isElem: true
        };
    }
}

function cleanAnGetNode(node) {
    let realNode = node;
    if (typeof node === 'string') {
        realNode = document.getElementById(node);
    }

    while (realNode.firstChild) {
        realNode.removeChild(realNode.firstChild);
    }
    return realNode;
}

/**
 * Render a component to the dom.
 * @param {string|Node} node the id or the node where the component must be rendered.
 * @param {Component} component the component to render.
 * @param {Store} store the store
 */
export function renderToDom(node, component, store = new Store()) {
    const event = new Event('beforerender');
    window.dispatchEvent(event);
    renderComponents(node, [component], store);
}

function renderComponents(node, components, store = new Store()) {

    const realNode = cleanAnGetNode(node);

    const componentList = [];

    flatten(components).filter(component => component !== undefined && component !== null)
        .map(component => convertToNode(component, store, componentList))
        .forEach(node => realNode.appendChild(node));

    componentList.forEach(component => component.componentDidMount());

    if (componentList.length) {
        const mutationObserver = new MutationObserver(() => {
            if (!document.body.contains(realNode)) {
                mutationObserver.disconnect();
                store.unsubscribeAll();
            }
            for (let index = store.componentsToUnmount.length -1; index >= 0; index--) {
                const component = store.componentsToUnmount[index];
                if (component.node && !realNode.contains(component.node)) {
                    component.componentDidUnmount();
                    store.componentsToUnmount.splice(index, 1);
                }
            }
            for (let index = store.componentsSubscribes.length - 1; index >= 0; index--) {
                const component = store.componentsSubscribes[index];
                if (component.component.node && !realNode.contains(component.component.node)) {
                    component.subscribes.forEach(({event, id}) => store.unsubscribeByEventAndId(event, id));
                    component.component.node = undefined;
                    store.componentsSubscribes.splice(index, 1);
                }
            }
        });


        mutationObserver.observe(document.body, {childList: true, subtree: true});
    }
}

/**
 * Render some elements into a string.
 * @param {Array} elements elements returned by {@link el} or primitive like string.
 * @return {string} html as a string.
 */
export function renderToString(...elements) {
    return flatten(elements).map(el => {
        if (!el.name) {
            return '' + (el.__asHtml || el);
        }
        const attributes = Object.keys(el.attrs)
            .filter(attribute => !attribute.startsWith('on') && el.attrs[attribute] !== undefined && attribute !== 'ref')
            .map(attribute => {
                const key = dasherize(attribute === 'className' ? 'class' : attribute);
                let value = el.attrs[attribute];
                if (key === 'style' && typeof value === 'object') {
                    value = Object.keys(value)
                        .map(key => '' + dasherize(key) + ':' + value[key])
                        .join(';');
                } else if (key === 'class' && typeof value === 'object') {
                    value = Object.keys(value).filter(classValue => value[classValue])
                        .map(dasherize)
                        .join(' ');
                }

                return ` ${key}="${value}"`
            })
            .join('');
        const content = renderToString(...el.children);
        return `<${el.name}${attributes}>${content}</${el.name}>`
    }).join('');
}


/**
 * Render some elements into a node.
 * @param {string|Node} node the id or the node where the component must be rendered.
 * @param {Array} elements elements returned by {@link el} or primitive like string.
 */
export function renderTo(node, ...elements) {
    renderComponents(node, elements)
}
